sysauto_script.script:
gs.info("inside");
var positions = gs.getProperty('x_1178602_corpor_0.u_emp_positions');
gs.info("positions" + positions);

var gt = new GlideDateTime();
gs.info("date");
var currentMonth = gt.getMonthLocalTime();
gs.info("Month" + currentMonth);
var quarter = false;
if (currentMonth == 1 || currentMonth == 4 || currentMonth == 7 || currentMonth == 10);
quarter = true;

if (quarter) {
    var emp = new GlideRecord('x_1178602_corpor_0_employee');
    emp.addQuery('position_level', 'IN', positions);
    emp.query();
    while (emp.next()) {
        emp.setValue('e_wallet_budget', 8000);
        emp.update();
        gs.info("e wallet budget added successfully");
    }
} else {
    gs.info("e wallet budget not added");
}
